-- Create "vex_documents" table
CREATE TABLE `vex_documents` (`id` bigint NOT NULL AUTO_INCREMENT, `vex_id` varchar(255) NOT NULL, `statement_vex_documents` bigint NOT NULL, PRIMARY KEY (`id`), UNIQUE INDEX `statement_vex_documents` (`statement_vex_documents`), INDEX `vexdocument_vex_id` (`vex_id`), CONSTRAINT `vex_documents_statements_vex_documents` FOREIGN KEY (`statement_vex_documents`) REFERENCES `statements` (`id`) ON UPDATE NO ACTION ON DELETE NO ACTION) CHARSET utf8mb4 COLLATE utf8mb4_bin;
-- Create "vex_statements" table
CREATE TABLE `vex_statements` (`id` bigint NOT NULL AUTO_INCREMENT, `vex_id` varchar(255) NOT NULL, `vex_document_vex_statements` bigint NOT NULL, PRIMARY KEY (`id`), UNIQUE INDEX `vex_document_vex_statements` (`vex_document_vex_statements`), INDEX `vexstatement_vex_id` (`vex_id`), CONSTRAINT `vex_statements_vex_documents_vex_statements` FOREIGN KEY (`vex_document_vex_statements`) REFERENCES `vex_documents` (`id`) ON UPDATE NO ACTION ON DELETE NO ACTION) CHARSET utf8mb4 COLLATE utf8mb4_bin;
